import React, { useState } from 'react';
import Task from './Task';

const ToDoList = () => {
  const [tasks, setTasks] = useState([
    { id: 1, assignedTo: 'User 1', status: 'Completed', dueDate: '12/10/2024', priority: 'Low', comments: 'This task is good' },
    { id: 2, assignedTo: 'User 2', status: 'In Progress', dueDate: '14/09/2024', priority: 'High', comments: 'This task needs attention' },
  ]);
  const [taskInput, setTaskInput] = useState({ assignedTo: '', status: '', dueDate: '', priority: '', comments: '' });
  const [editMode, setEditMode] = useState(false);
  const [currentTaskId, setCurrentTaskId] = useState(null);

  const addTask = () => {
    const newTask = { id: tasks.length + 1, ...taskInput };
    setTasks([...tasks, newTask]);
    setTaskInput({ assignedTo: '', status: '', dueDate: '', priority: '', comments: '' });
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const editTask = (task) => {
    setEditMode(true);
    setCurrentTaskId(task.id);
    setTaskInput(task);
  };

  const updateTask = () => {
    setTasks(tasks.map(task => task.id === currentTaskId ? taskInput : task));
    setEditMode(false);
    setTaskInput({ assignedTo: '', status: '', dueDate: '', priority: '', comments: '' });
  };

  return (
    <div className="container mt-5">
      <h2>Task Manager</h2>
      
      <div className="mb-4">
        <input type="text" placeholder="Assigned To" value={taskInput.assignedTo} onChange={(e) => setTaskInput({...taskInput, assignedTo: e.target.value})} />
        <input type="text" placeholder="Status" value={taskInput.status} onChange={(e) => setTaskInput({...taskInput, status: e.target.value})} />
        <input type="text" placeholder="Due Date" value={taskInput.dueDate} onChange={(e) => setTaskInput({...taskInput, dueDate: e.target.value})} />
        <input type="text" placeholder="Priority" value={taskInput.priority} onChange={(e) => setTaskInput({...taskInput, priority: e.target.value})} />
        <input type="text" placeholder="Comments" value={taskInput.comments} onChange={(e) => setTaskInput({...taskInput, comments: e.target.value})} />
        
        <button className="btn btn-primary ms-2" onClick={editMode ? updateTask : addTask}>
          {editMode ? 'Update Task' : 'Add Task'}
        </button>
      </div>

      
      <table className="table table-striped table-responsive">

        <thead>
          <tr>
            <th>Assigned To</th>
            <th>Status</th>
            <th>Due Date</th>
            <th>Priority</th>
            <th>Comments</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <Task key={task.id} task={task} onDelete={deleteTask} onEdit={editTask} />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ToDoList;
